import { Component, OnInit } from '@angular/core';
import { BaseService } from 'src/app/core/services/base.service';

interface LocalCharge {
  valid: string;
  bound: string;
  containerOwnership: string;
  perCode: string;
  containerStatus: string;
  chargeCode: string;
  currency: string;
  unitAmount: string;
  prepaidCollect: string;
  pop: string;
  effectiveDate: string;
  expiryDate: string;
}

@Component({
  selector: 'app-local-charge-master',
  templateUrl: './local-charge-master.component.html',
  styleUrls: ['./local-charge-master.component.scss']
})
export class LocalChargeMasterComponent implements OnInit {
  isLoading = false;
  charges: LocalCharge[] = [];

  constructor(private baseService: BaseService) {}

  ngOnInit(): void {
    this.loadCharges();
  }

  /** Dummy loader to simulate backend API call */
  loadCharges(): void {
    this.isLoading = true;

    // Simulate API delay
    setTimeout(() => {
      this.charges = [
        { valid: 'Y', bound: 'I', containerOwnership: 'COC', perCode: 'R20', containerStatus: 'F', chargeCode: 'BLF', currency: 'SGD', unitAmount: '$180.00', prepaidCollect: 'P', pop: 'SGSIN', effectiveDate: '10-10-2025', expiryDate: '31-10-2025' },
        { valid: 'N', bound: 'I', containerOwnership: 'COC', perCode: 'D45', containerStatus: 'F', chargeCode: 'BLF', currency: 'SGD', unitAmount: '$122.00', prepaidCollect: 'C', pop: 'SGSIN', effectiveDate: '17-09-2025', expiryDate: '18-09-2025' },
        { valid: 'Y', bound: 'I', containerOwnership: 'COC', perCode: 'ALL', containerStatus: 'E', chargeCode: 'CMX', currency: 'SGD', unitAmount: '$6.00', prepaidCollect: 'C', pop: 'SGSIN', effectiveDate: '01-09-2025', expiryDate: '14-09-2025' }
      ];

      this.isLoading = false;
    }, 1000);
  }
}
